import React, { useState, useEffect } from 'react';
//import logo from './logo.svg';
//import APICall from './API';
import Dropdown from './components/Dropdown/dropdown';
import axios from 'axios';
import './App.css';
//const { REACT_APP_CLIENT_SEC, REACT_APP_CLIENT_ID } = process.env;

function App() {
  const data = [
    { value: 1, name: 'A' },
    { value: 2, name: 'B' },
    { value: 3, name: 'C' },
  ];
  const clientSecret = 'f08b7f2916e0478596e57ec0f16f4b0a';
  const clientId = 'f9b121e7380a482f966c1dbb273db9e3';
  const [token, setToken] = useState('');
  const [genres, setGenres] = useState({
    selectedGenre: '',
    listOfGenresFromAPI: [],
  });

  useEffect(() => {
    //get initial token in order to access endpoints
    axios('https://accounts.spotify.com/api/token', {
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded',
        Authorization: 'Basic ' + btoa(clientId + ':' + clientSecret),
      },
      data: 'grant_type=client_credentials',
      method: 'POST',
    }).then((tokenResponse) => {
      // console.log(tokenResponse.data.access_token);
      setToken(tokenResponse.data.access_token);
      //get genres
      axios('https://api.spotify.com/v1/browse/categories?locale=sv_US', {
        method: 'GET',
        headers: { Authorization: 'Bearer ' + tokenResponse.data.access_token },
      }).then((genreResponse) => {
        setGenres({
          selectedGenre: genres.selectedGenre,
          listOfGenresFromAPI: genreResponse.data.categories.items,
        });
      });
    });
  }, [genres.selectedGenre, clientId, clientSecret]);

  return (
    <div className='App'>
      <form onSubmit={() => {}}>
        <Dropdown options={genres.listOfGenresFromAPI} />
        <Dropdown options={data} />
        <button type='submit'>Search</button>
      </form>
    </div>
  );
}

export default App;
